package com.tnsif.day2.breakdemo;

public class Continue {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
         
         for(int i=0;i<5;i++)
         {
        	 if(i%2!=0)
        	 {
        		 System.out.println(i);
        		 continue;
        	 }
        		 
         }
	}
}
